INSERT INTO `meituan_nanwan` VALUES ('4071', 'jin179235261', '283638961', 'https://img.meituan.net/avatar/80e506ef5a6ec7c0b2ceba7b2a37f9cc8898.jpg', '猴岛好小，一早上游泳池都没有猴子游泳！表演还行！团里人太少，导游不怎么搭理大家，基本属于自己逛，中午50元自助餐，导游吹得很好，进去一看，菜摆得不少，但是没什么想吃的，不想评价！椰田古寨的景区导游还挺好的！全程微笑讲解！很热闹，感觉不错！两个景区一共买了四张照片，吃了自助餐，没别的了！', '2016-05-26 08:49:03', '-1937451155:http://p0.meituan.net/shaitu/7814e58449c35b51ad7a8697a6e99a5345551.jpg;\n-1937868058:http://p1.meituan.net/shaitu/9d037080f9ba3ee8d960a68a7f37cd93118040.jpg;\n-1937810786:http://p0.meituan.net/shaitu/9c110a49f736074bba909457b861395e97838.jpg;\n', 'None');
