INSERT INTO `meituan_nanwan` VALUES ('4449', 'hjkxx3209', '220908876', '', '猴岛很好玩，坐了过海缆车，风景很漂亮！导游服务也不错，没有强制消费现象，猴岛上很多猴子，大家不要穿鲜艳的衣服，不要带食物，不要让包包的拉链拉开，重要的事情说三遍噢，。椰岛古镇就比较一般，整体感觉挺值得的！', '2016-01-22 22:22:43', '-1939646744:http://p0.meituan.net/shaitu/ca7f88cd983b6d668b7eda7dda2536b5439185.jpg;\n-1939739472:http://p1.meituan.net/shaitu/5beba7c47273028a31d582eee4a9fba5286400.jpg;\n-1939799817:http://p0.meituan.net/shaitu/ca1b40a528d225172e293d7864aef647462274.jpg;\n', 'None');
