INSERT INTO `meituan_nanwan` VALUES ('4712', 'GUj66160449', '80685888', '', '不错很好玩，就是路太远了，千万别拿方便袋装东西，猴子会抢东西吃。', '2015-04-09 19:43:54', '', 'None');
INSERT INTO `meituan_nanwan` VALUES ('4713', '宝贝小馋猪262626', '18150117', '', '小猴子很可爱的！但是真的会伤害人噢噢噢！里边不大还有精彩演出！值得一去！椰田古镇就是要你花钱的地方了！但是东西买不买的真的一点用也没用给大家一个借鉴吧', '2015-04-09 10:14:31', '', 'None');
INSERT INTO `meituan_nanwan` VALUES ('4714', '我是冷布丁', '43008438', '', '导游很好，猴岛很好玩，野生猴子就是不一样，我们看猴，看人', '2015-04-08 19:59:08', '', 'None');
INSERT INTO `meituan_nanwan` VALUES ('4715', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '值得一去的景点，猴子很可爱。导游也很帅。', '2015-04-08 19:45:58', '', 'None');
