INSERT INTO `meituan_nanwan` VALUES ('4750', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '猴岛上的猴子很可爱，非常好玩！下午的椰田古寨虽然有购物，但是又不强迫你买，不错！', '2015-03-16 21:45:38', '', 'None');
