INSERT INTO `meituan_nanwan` VALUES ('4834', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '还好吧，主要是陪小孩去的。但是也是被店家给骗了，小孩不到1米2，居然还要再团一张，这个太坑人。猴子也没有多少，导游说是有三千多只。。', '2015-02-16 09:31:18', '-1931185032:http://p1.meituan.net/shaitu/73435b9ccb40adb4936d6554bea8fe7b395930.jpg;\n-1931404433:http://p0.meituan.net/shaitu/e3ddbbb906e450179693f62152ea2a59197360.jpg;\n-1931342811:http://p1.meituan.net/shaitu/352bbe0f8c78de6eb5c4c63d3c10507a355805.jpg;\n-1931280800:http://p1.meituan.net/shaitu/26fc3db7c459baa4dd0d11aa605e57e8371632.jpg;\n-1931941126:http://p0.meituan.net/shaitu/1805bc398ceb75845246cd0e2745e05b228723.jpg;\n', 'None');
