INSERT INTO `meituan_nanwan` VALUES ('4841', 'dYq13917210749', '30294913', '', '最大的亮点是可以和小猴子零距离接触。令人难过的是这里管理一片混乱，和三亚相比宛如两个世界。所谓的自助午餐基本全素，餐厅内苍蝇乱飞，所有人可以随便抽烟', '2015-02-13 13:01:35', '', 'None');
