INSERT INTO `meituan_nanwan` VALUES ('4855', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '感觉这个猴岛越来越不行了，里面的猴子也越来越少，再加上我们去的那天刚好停电，缆车也做不了，进去以后什么都要排队，光是排队加起来就是大概有两个多小时，很多人等不及了，都要求自己走着上去，总之让人很是郁闷啊', '2015-02-10 14:50:44', '', 'None');
