INSERT INTO `meituan_nanwan` VALUES ('4863', '淡如水wei', '102958334', '', '很不错，超值，导游也很好，景色很漂亮，缆车很好，猴岛的表演很给力像在马戏团一样，就是人太多了，上山或下山都需要占排等很久，没有耐心的就不建议去了，', '2015-02-08 09:45:17', '-1941616855:http://p0.meituan.net/shaitu/0669ab5672cd10a769b24078f15b189665215.jpg;\n-1941583037:http://p0.meituan.net/shaitu/36294b2fc9d6559d6f74ab8e519c632379188.jpg;\n', 'None');
