INSERT INTO `meituan_nanwan` VALUES ('4891', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '导游小王很热情，过海缆车很好玩，小猴子很可爱，就是在岛上呆的时间太短了。下午去得玉石翡翠购物店很吭人，销售伪装成老板儿子和售货员演戏忽悠消费者，大多数游客骗了。', '2015-02-02 21:36:53', '', 'None');
