INSERT INTO `meituan_nanwan` VALUES ('4949', 'Hvf18975002881', '33943939', '', '团购比较划得来，猴岛去了很多次很喜欢，不过提供的饭菜就不敢恭维了，比学校食堂还难吃，这方面还是改进下吧，不然玩得挺开心吃饭就郁闷了', '2015-01-21 16:25:52', '', 'None');
