INSERT INTO `meituan_nanwan` VALUES ('4951', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '自然环境很好，猴子也非常可爱，就是一点希望要把周边垃圾清理好，太影响整体了。', '2015-01-19 21:21:01', '', 'None');
