INSERT INTO `meituan_nanwan` VALUES ('5098', '卫生纸360', '94148592', 'https://img.meituan.net/avatar/1a3119b540bd978c3f08b759a8801c5991330.jpg', '南湾猴岛很好玩，套票包含自助餐、船票、吊车、门票，而且里面的全部景点都不需要另外收费了，还是非常不错的，里面的服务人员也非常热情！玩得很开心！与之完全相反的是分界州岛，里面的很多景点需要另外收费，电车售票人员态度恶劣，上山的站点和去酒店的车不是一起的，问他们都是爱理不理的，我们就这样在太阳下找了半小时才找到，总之那天玩得很不开心！', '2014-07-15 10:00:39', '-1942629494:http://p0.meituan.net/shaitu/__48391039__8914283.jpg;\n-1942095375:http://p0.meituan.net/shaitu/__48391030__4704085.jpg;\n-1942629478:http://p1.meituan.net/shaitu/__48391044__4390451.jpg;\n-1942095359:http://p1.meituan.net/shaitu/__48391041__8230352.jpg;\n-1942110709:http://p1.meituan.net/shaitu/__48391045__5975784.jpg;\n-1942024475:http://p1.meituan.net/shaitu/__48391035__3351834.jpg;\n-1941941908:http://p1.meituan.net/shaitu/__48391034__2179081.jpg;\n', 'None');
