INSERT INTO `meituan_nanwan` VALUES ('5122', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '挺好玩的，就是自己去有点远。早上七点五十五从三亚火车站坐高铁到陵水，再坐公交车到中国银行下，再换乘陵水到新村的汽车，到新村下，前行400米，留到猴岛景点的入口了。好累呀！', '2014-04-25 13:52:45', '', 'None');
