INSERT INTO `meituan_qixianling` VALUES ('67', '七弦221', '42344811', '', '七仙岭的景色还是不错的，一路的热带雨林的植物，空气很好，很潮湿，山虽然不够高，但是走的石径挺累的，体力要够好才能怕完全程。去的时候还下雨，山顶的雾超大的，还好最后的攀岩部分爬的时候有雾，但是爬上山顶了雾就散了，突然觉得一览众山小，景色超级美的。还是推荐的。', '2018-08-20 13:11:31', '-1342882073:http://p0.meituan.net/shaitu/baafd01e217b72da58294bcdb13f0bdc123580.jpg;\n-1342978253:http://p1.meituan.net/shaitu/7962cd160b602a19e96382c3b81e66c4117099.jpg;\n-1342978252:http://p1.meituan.net/shaitu/dbff23a76fb90dcbb8c0b7b1a1a20906131285.jpg;\n-1342882072:http://p0.meituan.net/shaitu/0851a887f54fbb7ac7f74eebcea3bcd4396176.jpg;\n-1342882071:http://p0.meituan.net/shaitu/543433a1b4215745e71dc4f3f5ac994e352831.jpg;\n-1342882070:http://p1.meituan.net/shaitu/64a13535291a790071dae1f347e7b629229053.jpg;\n', 'None');
