INSERT INTO `meituan_qixianling` VALUES ('194', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '很一般！最好自己有车来！上下山来回4小时左右！海南本地身份证48一张票！没什么可看的景点！就是热带雨林！最顶上能拍几张不错的照片！其他没可看的！不建议来！着还不如去尖峰岭！山下的温泉酒店不错！！！！！！！！！！！！！！', '2018-04-09 17:44:06', '-1466889411:http://p0.meituan.net/shaitu/3372bcc03c3426d08dca739a17ae18dd4507563.jpg;\n-1466889410:http://p0.meituan.net/shaitu/652be74731e2d50b0d2b75e275f52e6e3963035.jpg;\n-1466988360:http://p0.meituan.net/shaitu/f944d9a1e9f81d2212a36debeb8b0d0e2482699.jpg;\n-1466889408:http://p0.meituan.net/shaitu/bab82a9123747726a78e59b73310e7392312070.jpg;\n-1466988362:http://p0.meituan.net/shaitu/4eb0d18d725929794bc52c0cc5287f424276821.jpg;\n-1466988361:http://p0.meituan.net/shaitu/d8d4f4103b63c56d3fd66999d841d98f3414742.jpg;\n-1466889409:http://p0.meituan.net/shaitu/271341b4876b6021e3cbcd6ab727b60f4777984.jpg;\n', 'None');
