INSERT INTO `meituan_qixianling` VALUES ('213', 'zj76058470', '80793815', '', '望山跑死马，说的没错的，从宾馆看起来不太远，朋友大力推荐，说七仙岭比五指山美很多，爬起来也有意义，说2300米，爬起来就知道了，根本就不是那么回事，好在空气好，环境也比较优美，早上8点多就进山了，大清早一路没什么人，也没怎么休息，12.30下山到出口，为了退房有点赶了，适合边走边玩，最后的顶太陡了，量力而行没有爬上去，安全第一，对了山上的矿区水14元一瓶，11点以后才有得卖，一定要带水，一圈下来，大汗淋漓的，很爽，下山腿也很爽，抖的厉害，晚上酸疼，平时锻炼少了，总而言之，不虚此行，很满意', '2018-03-18 22:19:52', '-1480410485:http://p0.meituan.net/shaitu/c898be73c4a00bbf100d4851fda82dcd211626.jpg;\n-1480410484:http://p0.meituan.net/shaitu/28d5cea0613f69cb261377cc97ca49cf2698490.jpg;\n-1480410483:http://p0.meituan.net/shaitu/0e936d1cbc9d5c38c50aa485437c7d383715356.jpg;\n-1480410482:http://p0.meituan.net/shaitu/b5fcbc168ad61c74e86eaa3ca59756083932150.jpg;\n', 'None');
