INSERT INTO `meituan_qixianling` VALUES ('1502', 'daij921', '21958148', 'https://img.meituan.net/avatar/f34be57dd0708c8d698e95b25873805a46497.jpg', '七仙岭温泉森林公园，海拔高度933米，路程2.3公里。我和老爹一起爬了3个多小时到顶，最后一点只有铁链攀到峰顶，老爹70了，我没让爬，我爬上山顶，风景真美。推荐给大家，喜欢登山的可以来这里，从三亚开车到景区2个小时左右。建议中午之前登山。', '2016-02-14 11:19:07', '-1937512469:http://p1.meituan.net/shaitu/fb1e5cfb27c21035f4d1a653e0c049ce395694.jpg;\n-1937519165:http://p0.meituan.net/shaitu/6c8a8f06992fb3b0b922b7831d888dda254451.jpg;\n-1937521505:http://p0.meituan.net/shaitu/81273fa58eb29eef68f9ae48af2c0584248838.jpg;\n-1938076198:http://p0.meituan.net/shaitu/a88acb09bc86e583bade1c14a4e622a2130195.jpg;\n-1937494399:http://p0.meituan.net/shaitu/49808ef1f603a3192b76e4c3caf20fb2223777.jpg;\n', 'None');
