INSERT INTO `meituan_qixianling` VALUES ('1514', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '雨林就自然风景来说感觉好过呀诺达，不过论开发和各种配套服务设施比呀诺达差距太大。登顶的感觉很棒，山顶非常冷啊，记得多带衣服。景区里上下山全程没有任何服务设施，所以要带些食品。这么好的地方希望未来可以变得更好！', '2016-02-09 18:33:57', '-2078283674:http://p1.meituan.net/shaitu/bb041db432dcad9a9096c176cdc95b6b283001.jpg;\n-2078229138:http://p1.meituan.net/shaitu/3138fb030e7258331dc6d136d819dc74251324.jpg;\n-2078283690:http://p1.meituan.net/shaitu/40272201ec0d157273c9eb39a3be01e969565.jpg;\n', 'None');
