INSERT INTO `meituan_qixianling` VALUES ('1521', 'akn203394654', '214822213', 'https://img.meituan.net/avatar/42e6e2f11766aa51ce9e52b10cea76e2128884.jpg', '景区保持了原始的风貌，古树古藤奇石清泉相映成趣，景致优美，环境清幽，参天的大树遮蔽了烈日的曝晒，可以缓步慢行，边走边歇，景区内标识清晰醒目，不时有里程及海拔的提示，可以给人以信心和鼓励，石阶一路通上去，由缓变陡，太小的孩子不建议登顶，因为下比较费劲哦。', '2016-02-04 18:58:33', '-2105696394:http://p0.meituan.net/shaitu/7a25c1862460816280df6509ef849ec11239759.jpg;\n-2105656740:http://p1.meituan.net/shaitu/2496e8fc34b1d5734306d118bd97b3361226712.jpg;\n-2105744545:http://p0.meituan.net/shaitu/395970c7ba5ced5d800663676ab0462c1314506.jpg;\n', 'None');
