INSERT INTO `meituan_qixianling` VALUES ('1525', '一瓮老酒', '305237418', 'https://img.meituan.net/avatar/dc66de779ad8f8a603ec44bb0ef54a5b2888.jpg', '七仙岭温泉不错，爬山要慎重考虑！真的好高啊！山路很长，但是登顶后还是感觉不错的。体力不充沛者不建议登顶。背包里要带上防风防雨的衣物，穿胶底登山鞋，最后登顶时必备。建议孩子和老人在谷内凉亭处折返，不要勉强登山。', '2016-02-01 13:56:04', '-2004526665:http://p1.meituan.net/shaitu/0a593f9ef0fa27682ced6a4578b6c062278666.jpg;\n', 'None');
