INSERT INTO `meituan_qixianling` VALUES ('1685', '小绿鸭鸭', '143996463', 'https://img.meituan.net/avatar/c7fa5bfdb82d36d43d230979655ec07727410.jpg', '山里的环境非常好，爬到山顶再下来大概五个小时！景区的道路还好，只是最基本的厕所设施都是摆设，全部上了锁，还是在清明小长假！在全省都在整治景区厕所的时候还能出现这样的问题，管理实在是太不专业了！', '2015-04-06 09:29:09', '', 'None');
