INSERT INTO `meituan_qixianling` VALUES ('1714', '霹雳小锥子', '30246665', '', '温泉非常好，雨林里树木种类好多，真是长见识了，老茎开花等天然奇观让人叹为观止，山真的是好高啊，下山都下了一个小时，顶峰的陡坡也是一个很大的挑战，登顶后给人一种前所未有的征服感，虽然累，但还是值得一游的!', '2015-02-11 18:54:59', '-2062224146:http://p0.meituan.net/shaitu/b119e79de3e7568e45bee7ecc91270ee90687.jpg;\n-2062298042:http://p0.meituan.net/shaitu/8db3d7aa842ad6fa2f172f4316162bee56827.jpg;\n', 'None');
