INSERT INTO `meituan_qixianling` VALUES ('1743', 'Elo8661753', '128561851', '', '回归原始的感觉，没有太多的装饰，没有商业气息，漫步在其中很舒服，只是最后一段实在太难爬了，如果修好了，年龄大的也可以到山顶看全景就更好了', '2014-12-08 11:11:14', '', 'None');
INSERT INTO `meituan_qixianling` VALUES ('1744', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '环境非常好，是个好地方，冬天来真是很开心', '2014-12-07 14:08:41', '', 'None');
