INSERT INTO `meituan_wenbifeng` VALUES ('2422', '汪小龟ww', '222209034', 'https://img.meituan.net/avatar/c0708628dd44040b102a242d98e813a818697.jpg', '文笔峰也是一去再去！！从前年去了之后……，每年都会去几次……反正离家又近，又不远，爬爬山、吃素斋、看看风景，挺好的！山上环境优美，又干净。设施也很健全。了解一下道教历史，登山远眺，心情舒畅，很不错，全满分。', '2016-03-14 23:05:59', '-2049320846:http://p0.meituan.net/shaitu/9a4480555e999ac42d204ac6fedc44a325201.jpg;\n-2049207085:http://p1.meituan.net/shaitu/97d01cf2afd8aad3acbead18c2e8749170977.jpg;\n-2049203555:http://p1.meituan.net/shaitu/ac94328a13d96d4ba640393102f1778a73173.jpg;\n', 'None');
