INSERT INTO `meituan_wenbifeng` VALUES ('3197', 'cdm晶莹的泪', '85750393', 'https://img.meituan.net/avatar/b8dde1ee6cb90b02bc79ead351f6e53a14823.jpg', '环境特别好 景区很干净 人也不多 没买电瓶车券 步行上去大约1小时 下山路很窄 两边热带植物环绕 景色宜人 在海口能够有这样的公园真好！团购的票 服务人员不好 看都不看就问我什么时候买的 我说刚买的 他说要2个小时以后才能用 一共也没几个游客 后来打电话给商家 发了验证码 商家有发传真 总之最后进去了 就是比较麻烦 如果自己不争取 真的要等2个小时。', '2015-07-18 15:01:34', '-1934924443:http://p1.meituan.net/shaitu/ebd3b3cad6704ac3e2975cf5064530581120125.jpg;\n-1934983809:http://p1.meituan.net/shaitu/0d8b8d659bc29d1f0c42a99ac077c363670156.jpg;\n-1935005063:http://p0.meituan.net/shaitu/8ff13dbfd2992c5fc9f4816f47d8d754678444.jpg;\n-1934941679:http://p0.meituan.net/shaitu/3460ecf6544f9b723165a28180760f891377833.jpg;\n-1934813012:http://p1.meituan.net/shaitu/9ee713d03dc114b38b6fadc13a22a485849945.jpg;\n-1935397114:http://p1.meituan.net/shaitu/df80a257771e20bce6cb27ae3f8acbb6806014.jpg;\n-1935335314:http://p1.meituan.net/shaitu/4750068088ad33dcc115ab0d628fd4db719934.jpg;\n', 'None');
