INSERT INTO `meituan_wenbifeng` VALUES ('3250', '吴清娇', '208943070', '', '设施完整，服务周到，环境优美，空气清新五，其实嗯，爬到山顶，还是挺有成就感的！还有比较有趣的事，爬到山顶的时候，看到远处有下雨而且看到了彩虹。最特别的是，我们准备下山的时候，就下雨了！我只想说的是太好了，幸亏爬完了才下雨。不过太阳好晒哦。所以同志们去的时候，最好是，戴上帽子，和防晒衣，还有，要准备雨伞。', '2015-06-20 22:14:08', '-1940718384:http://p0.meituan.net/shaitu/4eb11fb3a4254231f8b63185dd3d4b61270356.jpg;\n-1941353574:http://p1.meituan.net/shaitu/30e81ebc5f05847590f5ef40eddd89e3306997.jpg;\n-1940802575:http://p0.meituan.net/shaitu/1dea84f7815073c615fca1e1793e1845260285.jpg;\n-1940854263:http://p0.meituan.net/shaitu/a99fb7e3499c624b9bef4951f24d82e3473040.jpg;\n-1940718368:http://p0.meituan.net/shaitu/f249511c05e539440ab165f16e571a87236803.jpg;\n', 'None');
