INSERT INTO `meituan_xidao` VALUES ('1296', 'LyO113297261', '319641743', '', '碧水蓝天风景很美，门口取票也很方便，因为是到了景区才在美团上订的全票，需要等一个小时才能取票，在这期间在景区游客中心里吃了午饭，一盒海南粉，一盒凉粉都是20一盒，味道一般，里面的小纪念品什么的建议就不要买了，都懂得！坐船十来分钟就到岛上了，海水很蓝很清澈，海边有那种特别硌脚的说不清是什么的小石头，光脚走受不了！出来的时候人太多，排队站的脚疼！总的来说风景还是不错的，值得去！', '2018-01-20 17:19:37', '-1839518821:http://p0.meituan.net/shaitu/bdb68785aa85b3d36c6b5b635253812172971.jpg;\n-1839421267:http://p0.meituan.net/shaitu/6eb2f9a2730796b5e52ac7b7cc326646168920.jpg;\n-1839421266:http://p1.meituan.net/shaitu/b5f7378998a82097506b7d5e738e8f0b67654.jpg;\n-1839421265:http://p0.meituan.net/shaitu/1fcee86346aa3aaf69a5e0ee43263f44155228.jpg;\n-1839421263:http://p0.meituan.net/shaitu/8320e5e7f790b3ab921472c68a58ad38311041.jpg;\n-1839421262:http://p1.meituan.net/shaitu/c4b7c901069edfceab3f511d90c5dbc550370.jpg;\n', 'None');
