INSERT INTO `meituan_xidao` VALUES ('1590', 'tiger_afc', '264913077', '', '西岛名气没有蜈支洲岛大，但听当地的司机师傅介绍，水上项目并不比蜈支洲岛的少，而且正因为人没有那么多，可能玩起来会更加舒心。目前除了水上项目外，西岛真正开发了的区域并不大，没有什么景点，但本身的阳光沙滩，碧水晴天就让人心旷神怡。紧挨着的牛王岭一定要去逛逛，岭上可以看到三亚湾的壮丽景色。看看海景，不玩项目，3到4个小时左右。听说岛上还在加紧建设，准备升级到5A，也看到有工地在施工，值得期待。', '2017-12-01 21:07:35', '-1844902680:http://p1.meituan.net/shaitu/03a456df33d98bd6b3feeb1de68a00a3360519.jpg;\n-1844802684:http://p1.meituan.net/shaitu/5a11f74aa8098ce53fefdc20902773d5165249.jpg;\n-1844802683:http://p1.meituan.net/shaitu/60a5f818acf793d9c700e5fcddce65cb174178.jpg;\n-1844802682:http://p0.meituan.net/shaitu/fce8c2ae507e04f980babc5d5fe72fa9368083.jpg;\n-1844902678:http://p1.meituan.net/shaitu/2ec9305423454e7accb832598c570fb1148716.jpg;\n', 'None');
