INSERT INTO `meituan_xidao` VALUES ('1598', 'PZn109962525', '232366067', '', '自由行去的西岛 在美团上买的门票 要提前一个小时团才能检票。西岛的风景很美，没来过的朋友真的值得来玩玩，酒店价格也不贵200左右住的也很舒服哦！海鲜也不错，在美团上找人气旺的海鲜店即可。最后就是冬季来气温27度左右，也应该穿短袖，我带的秋天衣服热啊，那个阳光紫外线强比武汉的27度左右热多了……?', '2017-12-01 07:00:15', '-1844852071:http://p0.meituan.net/shaitu/7fea20bb2ed7464f3b62b3c1a85e3ede354940.jpg;\n-1844951982:http://p0.meituan.net/shaitu/9570259d04fe61e314dcb38a22fe76dc286618.jpg;\n-1844951983:http://p0.meituan.net/shaitu/bf192eeca59d9515148e6ac6d7caa4b0186769.jpg;\n-1844951984:http://p0.meituan.net/shaitu/f26e2cad7b77d8097a7a041253ad2ca0479049.jpg;\n', 'None');
