INSERT INTO `meituan_xidao` VALUES ('3619', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '环境非常干净，特别是卫生间非常干净，景区内处处风景，天涯海角有美好的寓意。儿子大爱这里的海，踩水玩了很久，这里有不花心的花，如果不是去过云南热植所，就不会认识。给大家讲了讲，做了一回免费导游。建议植物挂牌就更好了。', '2017-04-16 16:04:31', '-1887847289:http://p0.meituan.net/shaitu/9643db3edfa66309af02ad986fdf65de790285.jpg;\n-1887854341:http://p0.meituan.net/shaitu/b2d67344c2a2116a0f8a487dc2e680fc415828.jpg;\n', 'None');
