INSERT INTO `meituan_xidao` VALUES ('4325', 'fyz682313522', '659774475', '', '昨晚下雨，原以为今天天公不作美，上不了岛，今晨和家人仍早早的到景区换票，但去太早了等到八点才换了票。进岛后游了一圈，没走一小时就把岛上景区走完，有点失望，太阳出来后乘电瓶车上牛王岭，牛王岭还好玩，站在礁石上眺望大海别一样情景，美…不过景区门票太贵，牛王岭属景区一处还需购40元电瓶车才能进，好多地都圈起不让去，游泳后冲凉水洗一次还需10元钱，感觉商业气息浓，总之去了还是不后性', '2017-02-09 16:45:47', '-1895882988:http://p1.meituan.net/shaitu/c08fea0ef06c4bfd6ccfea15a9fc27ee172948.jpg;\n-1896191673:http://p1.meituan.net/shaitu/05f1ddbc1ac1a0c6189eadf3c9722365233515.jpg;\n-1896204837:http://p1.meituan.net/shaitu/0e674a2e3b3a06a30578e03437083025215195.jpg;\n-1896544626:http://p1.meituan.net/shaitu/af74a887e0f800936cee9dc0876c9c79266699.jpg;\n-1896146783:http://p1.meituan.net/shaitu/695d1b1db32f1e4ac605c4e28ed15570339020.jpg;\n-1896064811:http://p0.meituan.net/shaitu/8e600f09510d1855e39f8ed7b7559ab2392282.jpg;\n-1896688198:http://p1.meituan.net/shaitu/3ae421274aa0eb87272a581048382016238495.jpg;\n', 'None');
