INSERT INTO `meituan_xidao` VALUES ('5503', 'QKO810028688', '208518455', '', '西岛水很清澈。风景还是很不错可以看到南海观音。我们玩了三小时被晒得不行。岛上的牛王岛要另外给40才能进。我们在里边看到了?。?️。是原生态的还可以。只是这个导演一点不人性化。我们就住在西岛附近非要我们去三亚湾集合叫我们等车的地方还不是上车的地方。找了好久。除了导游其他都很好。玩的还是开心', '2016-10-24 09:35:51', '-1911006220:http://p1.meituan.net/shaitu/3050b9ff998c5534f6d844ed4522b4aa385789.jpg;\n-1911806086:http://p0.meituan.net/shaitu/11cdaeb52f5ae1e4a75de57c8f54df52353819.jpg;\n-1911131936:http://p1.meituan.net/shaitu/da8f3a595af5177e56de2df3e41aa223257570.jpg;\n-1911720266:http://p0.meituan.net/shaitu/1d6f373d2359b5a2d444e5b57b29fe26489009.jpg;\n-1911028372:http://p1.meituan.net/shaitu/bb49ede52ee97e2e1b802cd86752843f158148.jpg;\n-1911229199:http://p0.meituan.net/shaitu/b8ca33393bdc1de8cff205d936f5812a400250.jpg;\n-1911242233:http://p1.meituan.net/shaitu/d39409c82ff4db104e110bc6db7fa195306668.jpg;\n-1911272483:http://p0.meituan.net/shaitu/bfd03719ef777b375df3e844fd933159465700.jpg;\n', 'None');
