INSERT INTO `meituan_xidao` VALUES ('5639', '福刚哥好帅', '46457769', 'https://img.meituan.net/avatar/84a197866ad7b5345670bc4ee8f8ae4416877.jpg', '工作人员很周到，海水非常清澈，非常清澈！\n顺便提醒下：\n1.去网络窗口取门票和电动车票，要带身份证；\n2.注意防晒！！！刚哥就被晒伤了……\n3.提前带毛巾，冲淡时需要用，冲淡费用10元每人；\n4.游泳圈可以到场租，30元一个另付押金30元\n5.躺椅100元每人，不限时', '2016-10-07 17:29:33', '-1914254472:http://p1.meituan.net/shaitu/0cdda30886a2e55dc3d22c41befe9a31251649.jpg;\n-1914915650:http://p0.meituan.net/shaitu/37187043e11363513ec06d9b8fe0a23e170162.jpg;\n-1914280972:http://p0.meituan.net/shaitu/d72579f3e4b4888273a80b07b158ae78253671.jpg;\n-1914550901:http://p1.meituan.net/shaitu/9129d54d8534b1046e7f4fa5ddd94b95147747.jpg;\n-1914511647:http://p0.meituan.net/shaitu/c1b51063751108d5c70881d868cb2ae6164210.jpg;\n-1914514857:http://p0.meituan.net/shaitu/3c1f74546a0c9ccd1d0fe7ae4bafb2d4233046.jpg;\n-1914546019:http://p1.meituan.net/shaitu/65b031c219c18ac9a0321d3973e28c50210673.jpg;\n-1914524189:http://p0.meituan.net/shaitu/402abb5acc0142cf756c83ae4850ee9584749.jpg;\n', 'None');
