INSERT INTO `meituan_xidao` VALUES ('5969', '曾jianwen', '54147541', 'https://img.meituan.net/avatar/8a8d27ebcdfa4a545eee91bafb96f6e712515.jpg', '风景很漂亮，而且消费比蜈支洲便宜多了，不坐电瓶车也基本上可以玩全岛，还去潜水，价格也比蜈支洲便宜。感觉比较实惠', '2016-09-01 20:25:47', '', 'None');
