INSERT INTO `meituan_xidao` VALUES ('6162', 'zTX127872776', '277760595', '', '当时准备去蜈支洲岛，后来三亚的朋友建议去西岛或分界洲，考虑时间和还车给朋友等因素，前一天晚上买票去西岛，138一人（含门票20，往返船票，电动车票），西岛还是没有辜负我的期待，非常漂亮，特别是牛什么岛，风景如画，真想在岛上住一晚。', '2016-07-31 23:32:16', '-2106425789:http://p1.meituan.net/shaitu/2eb85787b0572b1261a790241aa5663a158352.jpg;\n-2106468928:http://p0.meituan.net/shaitu/162d5b8c57c5cd9e5e29a9b4ff46ac8d121121.jpg;\n', 'None');
