INSERT INTO `meituan_xidao` VALUES ('6390', 'hommme', '9462320', '', '西岛上基本就是潜水和玩项目，有个潜水基地。岛上全是饮食和购物的。淡季人也不少。牛王岛上风景比较好，都是拍婚纱的。如果天不太热，可以慢慢转转拍拍风景。去礁石边泡泡脚也是不错的。如果想体验岛上民宿，可以联系在岛上工作的原住民。', '2016-06-24 16:18:04', '-2147272151:http://p1.meituan.net/shaitu/8173f124391625b8019922a69d16e827390932.jpg;\n-2147272150:http://p0.meituan.net/shaitu/5e323f587ea59bc23d5bb55240b87cae369641.jpg;\n-2147272149:http://p1.meituan.net/shaitu/9f7e4bbeb0b4b5e20660e6f2c4466db8434056.jpg;\n', 'None');
