INSERT INTO `meituan_xidao` VALUES ('6404', 'inE153914723', '101335942', '', '除了热还是热，除了蜈支洲岛，感觉西岛还是比其他岛屿热闹些。三亚的风景很不错，照的海边照片，根本不用ps都是很美很美的，很遗憾海抢降落伞没来得及坐，下车有机会去一定玩，还有海边游泳也没够，下次要去岛上住海景房。', '2016-06-20 21:24:10', '-1934798904:http://p0.meituan.net/shaitu/da26472645cb442ac7c9f34453afc1be433721.jpg;\n-1934889600:http://p1.meituan.net/shaitu/8492c41a0bcb6842741e658271169943501323.jpg;\n-1935580814:http://p0.meituan.net/shaitu/6fd0df1178ff569a7fc935036bb666ea518160.jpg;\n-1934996613:http://p0.meituan.net/shaitu/2de5dca2b18d8977894099f45e87bd62264869.jpg;\n', 'None');
