INSERT INTO `meituan_xidao` VALUES ('6410', '猫猫辣味', '114828804', '', '西岛的环境不错，躺椅太***，我问多少钱能坐多久，他说100想坐多久都可以，结果屁股还没坐热，服务员过来说她们要下班了，我也是醉了，哎，游玩项目建议买套票划算，天气太热晒黑了都，风景不错', '2016-06-19 20:13:04', '-1934794924:http://p1.meituan.net/shaitu/659047ddcbd92e68262c565b13f9e63c936158.jpg;\n-1935020261:http://p0.meituan.net/shaitu/3fb8d5079cd8ae888a5e061cd0f0def2709979.jpg;\n-1935028765:http://p0.meituan.net/shaitu/8267d451193d1e5de3719fae1c58d3c6980741.jpg;\n', 'None');
