INSERT INTO `meituan_xidao` VALUES ('6483', 'flower1124', '7414609', '', '没去蜈支洲岛，选择了西岛，因为看了美团上其他评价都说蜈支洲岛一票通比西岛贵而且还人多，排队等好久玩不了几个项目！但我也是登岛时间晚了些，到那都12点了，吃了自助餐后就马不停蹄去潜水了！人不多去了就能玩，最喜欢远海潜水，看了好多新奇的鱼和珊瑚，当然水下压力也是不能忽视的，下去耳膜受不了的会好疼（女王大人就没受得了，没两下就上岸了）一下午下来玩了堡礁潜水，远海潜水，摩托车观海，拖人伞，香蕉船，飞鱼船，快艇', '2016-06-04 08:27:42', '-1928445619:http://p0.meituan.net/shaitu/782144207ad5c969043955f5f15b55e5124229.jpg;\n-1928465501:http://p1.meituan.net/shaitu/02a053b074cdcc49652226ea11340438149630.jpg;\n-1928427753:http://p1.meituan.net/shaitu/30eb34f4847ba3674908a05beb56b7e2180706.jpg;\n-1928465485:http://p1.meituan.net/shaitu/6171ec3881016bcba192dbcb6243ce6b109175.jpg;\n', 'None');
