INSERT INTO `meituan_xidao` VALUES ('6861', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '纯净的西岛！细腻的白沙！清透的海水！能见度可达9-12米！带孩子们在海边沙滩踏浪都玩了两个小时！晒黑黑的！但是孩子们很尽兴！捡贝壳！还有牛王岭一定要去的！很多人说没有意义！出来玩最大的意义是发现美！获取开心！而不是吹毛求疵！那就家里呆着别来了！牛王岭孩子们一鼓作气爬上来！海浪波涛汹涌！很壮观！它的西面！看着太阳逐渐下山！真的是美的都不可方物！天气好！温度适中，所以陪着美景，真的超级赞！老人孩子玩的很好！建议顺带开展一个可以带小朋友骑单车的项目嘛！', '2016-04-01 13:53:11', '-1940919140:http://p1.meituan.net/shaitu/1c14da0c52c11ce2211c52e9c290f40f580832.jpg;\n-1941091309:http://p1.meituan.net/shaitu/aa20c4020408cdc92875331b7c2a0359664064.jpg;\n-1941622614:http://p0.meituan.net/shaitu/001c18c3315c07575c48f748af8f99d91089797.jpg;\n-1941123703:http://p1.meituan.net/shaitu/3d4a4eff161f02654f56d8be202008ea867818.jpg;\n-1940988992:http://p0.meituan.net/shaitu/c816c0d387df9de2f0f8cc5f64ea267e951479.jpg;\n', 'None');
