INSERT INTO `meituan_xidao` VALUES ('7348', 'GkW27320901', '53134018', 'https://img.meituan.net/avatar/b48bf3b74cead9f7b91b3049e9ff52f722975.jpeg', '和朋友一起带着孩子去的，出票速度快，九点多到的景区，到网络售票窗口很快就取到票了，坐的是游船上去的，看评价里有建议坐快艇的，不过我们没赶上，大船还好，孩子坐也没觉得晕船，西岛的海水好清，海边能捡到漂亮的贝壳和小枝的珊瑚，挺有乐趣的，怕人多不好排队没有玩潜水，初二去的玩的人不多，后悔没玩通项那个了，有机会会玩个通项的，牛王岭虽然小，可景色很特别，有大小洞天的感觉了，很苍凉雄壮，建议西岛景区加大宣传力度，蜈支洲就做的很不错，其实景色一样的美', '2016-02-11 22:15:19', '-2097521231:http://p1.meituan.net/shaitu/9ebe6d03bebd42ed03b66579b0cd31b958869.jpg;\n-2097539269:http://p0.meituan.net/shaitu/b0f636d859dc0c86e1af5a7e333916bc47214.jpg;\n-2097521838:http://p1.meituan.net/shaitu/914db47c5a78d47c357b2311c755af5941775.jpg;\n', 'None');
