INSERT INTO `meituan_xidao` VALUES ('7396', '丁有皮', '32387861', '', '网上团购提前一小时，非常重要哟！建议大家网购，比现场买少了近七十元。坐快艇非常刺激，岛上除了花钱的娱乐项目其它可以耍的太少了，观光都没有多大的范围和空间，相对这么贵的门票有点不值，当然如果去潜水或玩滑翔伞还是可以的', '2016-02-05 07:01:45', '-2013827233:http://p1.meituan.net/shaitu/e2929df50af7e75b23eb05768f0d876f439909.jpg;\n-2013661064:http://p0.meituan.net/shaitu/a9886c1c79ffb269bb10c7263ba65cd3160158.jpg;\n-2013661080:http://p0.meituan.net/shaitu/1d14f4d5a69d0464ff170b1856f1c575206999.jpg;\n', 'None');
