INSERT INTO `meituan_xidao` VALUES ('7923', 'aAW92928144', '118352482', '', '西岛值得一游。但是里面每个项目都要收费，而且很贵，如果是全家游建议大家买含游览车的套票，我们只买了门票➕船票，一家人又花了几百块钱（游览车40元/人）如果想潜水最好在网上买，现场都是几百一个项目！游泳的话建议带泳圈，在里面租一个要30，我在外面买一个才20！', '2015-08-05 23:49:10', '-1937802524:http://p0.meituan.net/shaitu/1c90a5730f729c7d10b6d4cb9d7bc759487943.jpg;\n-1937864500:http://p1.meituan.net/shaitu/431fd344ecd719838907ff2e1026c284544719.jpg;\n-1937974523:http://p0.meituan.net/shaitu/94dec3f4280277f840e713fe5832c0d6309130.jpg;\n', 'None');
