INSERT INTO `meituan_xidao` VALUES ('7952', '快乐0317', '23550547', 'https://img.meituan.net/avatar/d00049bd9b127cb09db63b6603dc504f18325.jpg', '这个团劵是我最差劲的一次，我游玩头一天团的说是不用预约，结果第二天到西岛换票人家说没有，我立即打电话到美团可是美团客服说他家就没有这个活动的票价，我晕为什么你家没有我还能在里面团呢？马上申请退款，可是到现在也说申请审批不成功，继续联系希望得到解决！至于景区的环境还是不错值得去的。', '2015-07-11 16:47:07', '', 'None');
