INSERT INTO `meituan_xidao` VALUES ('8139', '拥123', '86034762', '', '确实挺好的唯一不足就是五点半就清岛了，除了住在里面酒店的人都得离开、海边还有小小海螺 什么的可以捡，在三亚湾那些都没有这儿水清、', '2015-01-25 22:07:46', '', 'None');
