INSERT INTO `meituan_xinglong` VALUES ('56', '猪之妈', '63384392', 'https://img.meituan.net/avatar/__47997398__3859601.jpg', '￼\n兴隆热带植物园里种植着数千种热带植物，有很多品种是頻临灭绝的珍稀物种。跨进植物园，立刻就感到清凉爽快。迎面而来的是满眼的青翠，阵阵花香沁人肺腑。高大的乔木、团簇簇的灌木、枝头挂籽的果木让你目不暇接，间杂着姹紫嫣红的花草，让你一下子掉进了植物的海洋里。', '2018-08-21 21:18:29', '-1341047903:http://p1.meituan.net/shaitu/534a9c719a1a57368ad16c3705082718904711.jpg;\n-1341047902:http://p0.meituan.net/shaitu/2899a342cce57c455ad823aaebbf15c3794488.jpg;\n-1340952792:http://p1.meituan.net/shaitu/1b8f77d65cbfd8aac6a23ddee96e5bc5730476.jpg;\n-1340952791:http://p1.meituan.net/shaitu/39fca82c774abb439f32d8391d1de918770587.jpg;\n-1340952790:http://p1.meituan.net/shaitu/84c63d5e662081010fde8023415abab1690125.jpg;\n-1341047901:http://p1.meituan.net/shaitu/9eed266284ba2e2aafeff1f3af9c3bcb700385.jpg;\n-1340952789:http://p0.meituan.net/shaitu/24a26b81bf7293f71665a5491177c18b825775.jpg;\n', 'None');
