INSERT INTO `meituan_xinglong` VALUES ('387', 'ZgF162339999', '75559877', 'https://img.meituan.net/avatar/9b9be2358160187c8bb00271231a1ef096466.jpg', '北方人可以去看看，里面的植物种类繁多，印象最深的是看见了结在树上的菠萝蜜，成熟的菠萝蜜已经开裂，香味浓郁，还有各种特色植物：可可、咖啡等！地方不大，走走停停有一个多小时就够了！电影《芳华》取过景，看电影时没有注意到？', '2018-01-26 13:34:45', '', 'None');
