INSERT INTO `meituan_xinglong` VALUES ('601', '念旧920', '108432664', '', '下单后一个小时才可以取票，去逛园的时候最好穿长裤或者带驱蚊虫的，被咬了四十多包，很小的虫子，很痒，但是景色不错，可以看到好多珍贵的植物。', '2017-06-23 21:00:37', '-1878184069:http://p1.meituan.net/shaitu/4bddc1460ef873473f6d2489a472441c452380.jpg;\n-1877599624:http://p1.meituan.net/shaitu/06a2a1e52996d0f3158b7c954fd2f09c224846.jpg;\n-1877664988:http://p1.meituan.net/shaitu/05d68469753a4f397b38f2ade3e866e9434522.jpg;\n-1878428687:http://p0.meituan.net/shaitu/49fe90776a66dd35ae4a529132e8f4d7219549.jpg;\n-1878450851:http://p0.meituan.net/shaitu/e8f687109ecceae794d956ca1de8e975200276.jpg;\n-1878808326:http://p0.meituan.net/shaitu/4cdd320a76c4949c3029eb6eafcb33e2228019.jpg;\n-1878174361:http://p0.meituan.net/shaitu/6f69b025d4e65ef62096b02fb3fce669266180.jpg;\n-1878142237:http://p1.meituan.net/shaitu/c9f3556134eca727fdac6e3444a1fef4200766.jpg;\n', 'None');
