INSERT INTO `meituan_xinglong` VALUES ('896', 'gDD723798554', '316642507', '', '兴隆热带植物园是万宁和兴隆地带游玩的必游之地。园内绿植丰富，游玩中我们进一步认识了耶子树、槟榔树、菠萝蜜树等和树上的果实。也见到了景区内独有分支椰子树、铁西瓜树、胡椒树、可可树，胡椒树和可可树上可是果实累累的呦！还有各种花，包括各色蝴蝶蓝、红掌、白掌，独特的空中花园也令人感觉很新奇呢！还要告诉大家，椰奶咖啡☕也不错呦！', '2017-02-10 14:22:21', '-1895745896:http://p0.meituan.net/shaitu/fbf599883749338d6698f3111e40d1c5472731.jpg;\n-1895773172:http://p0.meituan.net/shaitu/6097a78925adf6fd38461e90ad536969448515.jpg;\n-1895895200:http://p1.meituan.net/shaitu/309067c2e53f96b42f9a8404dd6b79e5317933.jpg;\n', 'None');
