INSERT INTO `meituan_xinglong` VALUES ('1704', '虎啸塞北', '36834295', 'https://img.meituan.net/avatar/d1935546a164c2ff8ae13da91cbe0aaa443249.jpg', '美丽的热带植物园，内有人工湖，郁郁葱葱，隶属农业部热带植物研究所，主要以种植培育咖啡、香草兰和胡椒为主，占地600亩，游览区不大，还有许多职工住宅区占了一部分。游览最后免费品茶、咖啡、可可。后购物自愿，过分夸张园内产品。来兴隆值得一去！美团需提前一小时预约！', '2016-02-08 20:18:39', '', 'None');
